    long SpacetimeCompressionFieldMetadata = -1;

    int ControllerXCoordinate;
    int ControllerYCoordinate;
    int ControllerZCoordinate;

    @Override
    public boolean checkMachine_EM(IGregTechTileEntity iGregTechTileEntity, ItemStack itemStack) {

        int ControllerXCoordinate = iGregTechTileEntity.getXCoord();
        int ControllerYCoordinate = iGregTechTileEntity.getYCoord();
        int ControllerZCoordinate = iGregTechTileEntity.getZCoord();

        if (structureCheck_EM("main", 16, 16, 0)) {

            switch (iGregTechTileEntity.getFrontFacing()) {
                case 0:
                    SpacetimeCompressionFieldMetadata = iGregTechTileEntity.getWorld().getBlockMetadata(ControllerXCoordinate, ControllerYCoordinate + 2, ControllerZCoordinate);
                    break;
                case 1:
                    SpacetimeCompressionFieldMetadata = iGregTechTileEntity.getWorld().getBlockMetadata(ControllerXCoordinate, ControllerYCoordinate - 2, ControllerZCoordinate);
                    break;
                case 2:
                    SpacetimeCompressionFieldMetadata = iGregTechTileEntity.getWorld().getBlockMetadata(ControllerXCoordinate, ControllerYCoordinate, ControllerZCoordinate + 2);
                    break;
                case 3:
                    SpacetimeCompressionFieldMetadata = iGregTechTileEntity.getWorld().getBlockMetadata(ControllerXCoordinate, ControllerYCoordinate, ControllerZCoordinate - 2);
                    break;
                case 4:
                    SpacetimeCompressionFieldMetadata = iGregTechTileEntity.getWorld().getBlockMetadata(ControllerXCoordinate + 2, ControllerYCoordinate, ControllerZCoordinate);
                    break;
                case 5:
                    SpacetimeCompressionFieldMetadata = iGregTechTileEntity.getWorld().getBlockMetadata(ControllerXCoordinate - 2, ControllerYCoordinate, ControllerZCoordinate);
                    break;
            }

            return true;
        }
        return false;
    }

    @Override
    public GT_Multiblock_Tooltip_Builder createTooltip() {
        final GT_Multiblock_Tooltip_Builder tt = new GT_Multiblock_Tooltip_Builder();
        tt.addMachineType(translateToLocal("gt.blockmachines.multimachine.em.blackholegenerator.name"))   // Machine Type: Black Hole Generator
                .addInfo(translateToLocal("gt.blockmachines.multimachine.em.blackholegenerator.desc.0"))  // Controller block of the Black Hole Generator
                .addInfo(translateToLocal("gt.blockmachines.multimachine.em.blackholegenerator.desc.1"))  // Uses a black hole to generate power
                .addInfo(translateToLocal("tt.keyword.Structure.StructureTooComplex")) // The structure is too complex!
                .addSeparator()
                .beginStructureBlock(33, 33, 33, false)
                .addEnergyHatch(translateToLocal("tt.keyword.Structure.AnyHighPowerCasing1D"), 1) // Energy Hatch: Any High Power Casing with 1 dot
                .addMaintenanceHatch(translateToLocal("tt.keyword.Structure.AnyHighPowerCasing1D"), 1) // Maintenance Hatch: Any High Power Casing with 1 dot
                .toolTipFinisher(CommonValues.TEC_MARK_EM);
        return tt;
    }

